sys_script_include.script:
var ComletaEmail = Class.create();
ComletaEmail.prototype = Object.extendsObject(global.AbstractAjaxProcessor, {

    getEmail: function(){
		//prendi record degli user
		var usoRecord = new GlideRecord('sys_user');
		//prendi tutti i parametri di user id: nome cognome email ecc
		//this si riferisce a sysparm_userID
		usoRecord.get(this.getParameter('sysparm_userID'));
		//ritorna le email
		return usoRecord.email + "";

	},
	// si riceve la tipologia che si possiede  
	type: 'ComletaEmail'
});
